class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.begin(), horizontalCut.end(), greater<int>());
        sort(verticalCut.begin(), verticalCut.end(), greater<int>());

        long long a = 1, b = 1;
        long long  c = 0;
        long long  d = 0, e = 0;


        while (d < horizontalCut.size() && e < verticalCut.size()) {
            if (horizontalCut[d] > verticalCut[e]) {
                c += horizontalCut[d] * b;
                a++;
                d++;
            } else {
                c += verticalCut[e] * a;
                b++;
                e++;
            }
        }


        while (d < horizontalCut.size()) {
            c += horizontalCut[d] * b;
            a++;
            d++;
        }
        while (e < verticalCut.size()) {
            c += verticalCut[e] * a;
            b++;
            e++;
        }

        return c;
    }
};
