class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,bool>> cut;
           for(int i=0;i<m-1;i++){
            cut.push_back({horizontalCut[i],true});
           }
           for(int i=0;i<n-1;i++){
            cut.push_back({verticalCut[i],false});
           }
           sort(cut.rbegin(),cut.rend());
           int hcut=1,vcut=1;
           long long totalcost=0;
           for(auto &c:cut){
            long long cost=c.first;
            bool ishor=c.second;
            if(ishor){
                totalcost+=cost*vcut;
                hcut++;
            }
            else{
                totalcost+=cost*hcut;
                vcut++;
            }
           }
           return totalcost;
    }
};
