class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<pair<int,int>> pq;

        for(auto i:horizontalCut){
            pq.push({i,0});
        }
        for(auto i:verticalCut){
            pq.push({i,1});
        }

        long long cost=0;
        long long hc = 1;
        long long vc = 1;

        while(!pq.empty()){
            int c = pq.top().first;
            int a = pq.top().second;
            pq.pop();

            if(a==1){
                cost += (hc*c);
                vc++;
            }else{
                cost += (vc*c);
                hc++;
            }
        }
        return cost;
    }
};
