#include <vector>
#include <algorithm>

using namespace std;

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        // Sort the cuts in descending order
        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());

        int hCuts = 1, vCuts = 1;
        long long cost = 0;
        int i = 0, j = 0;

        // Use a greedy approach to choose the highest cost cut available
        while (i < horizontalCut.size() && j < verticalCut.size()) {
            if (horizontalCut[i] >= verticalCut[j]) {
                cost += static_cast<long long>(horizontalCut[i]) * vCuts;
                hCuts++;
                i++;
            } else {
                cost += static_cast<long long>(verticalCut[j]) * hCuts;
                vCuts++;
                j++;
            }
        }

        // Add the remaining horizontal cuts
        while (i < horizontalCut.size()) {
            cost += static_cast<long long>(horizontalCut[i]) * vCuts;
            i++;
        }

        // Add the remaining vertical cuts
        while (j < verticalCut.size()) {
            cost += static_cast<long long>(verticalCut[j]) * hCuts;
            j++;
        }

        return cost;
    }
};
