class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& hh, vector<int>& vv) {
        vector<int> h = hh, v = vv;
        sort(h.begin(), h.end());
        sort(v.begin(), v.end());
        reverse(h.begin(), h.end());
        reverse(v.begin(), v.end());
        long long pl = 0, pr = 0;
        long long ans = 0;
        while (pl != n - 1 || pr != m - 1) {
            if (pl == n - 1)
                ans += (pl + 1) * v[pr ++];
            else if (pr == m - 1)
                ans += (pr + 1) * h[pl ++];
            else if (h[pl] > v[pr])
                ans += (pr + 1) * h[pl ++];
            else
                ans += (pl + 1) * v[pr ++];
        }
        return ans;
    }
};
