class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans=0;
        vector<vector<int>>a;
        for(auto x : h)
        {
            a.push_back({x,0});
        }
        for(auto x : v)
        {
            a.push_back({x,1});
        }
        sort(a.begin(),a.end(), greater<>());

        long long hc=1;
        long long vc=1;

        for(auto x : a)
        {
            if(x[1]==0)
            {
                ans+=(1LL*vc*x[0]);
                hc++;
            }
            else
            {
                ans+=(1LL*hc*x[0]);
                vc++;
            }
        }
        return ans;
    }
};
