class Solution:
    def minimumCost(self, m: int, n: int, h: List[int], v: List[int]) -> int:
        h.sort()
        v.sort()
        h.reverse()
        v.reverse()
        hi,vi,hc,vc=0,0,1,1
        ans=0
        while 1:
            if hi>=len(h) or vi>=len(v):
                break
            if v[vi]<=h[hi]:
                ans+=vc*h[hi]
                hi+=1
                hc+=1
            else:
                ans+=hc*v[vi]
                vi+=1
                vc+=1
        while 1:
            if hi>=len(h):
                break
            ans+=vc*h[hi]
            hi+=1
            hc+=1
        while 1:
            if vi>=len(v):
                break
            ans+=hc*v[vi]
            vi+=1
            vc+=1
        return ans
