class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
         long long ans = 0;
         long long hCutSum = 0;
         long long vCutSum = 0;
         priority_queue<pair<int, bool>> pq;
         for(int c:horizontalCut){
            hCutSum += c;
            pq.push({c, true});
         }
         for(int v:verticalCut){
            vCutSum += v;
            pq.push({v, false});
         }
         while(!pq.empty()){
            int nowValue = pq.top().first;
            bool isHorizontal = pq.top().second;
            pq.pop();
            ans += nowValue;
            if(isHorizontal) {
                ans += vCutSum;
                hCutSum -= nowValue;
            } else {
                ans += hCutSum;
                vCutSum -= nowValue;
            }
         }
         return ans;
    }
};
