class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        hc.push_back(0);
        sort(hc.rbegin(), hc.rend());
        vc.push_back(0);
        sort(vc.rbegin(), vc.rend());

        int ind_h = 0, ind_v = 0;
        long long hs = 1, vs = 1;
        long long total = 0;

        while (ind_h < hc.size() - 1 || ind_v < vc.size() - 1) {
            if (ind_h < hc.size() - 1 && (ind_v >= vc.size() - 1 || hc[ind_h] >= vc[ind_v])) {
                total += static_cast<long long>(hc[ind_h]) * vs;
                ind_h++;
                hs++;
            } else {
                total += static_cast<long long>(vc[ind_v]) * hs;
                ind_v++;
                vs++;
            }
        }

        return total;
    }
};
