class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
    sort(a.begin(), a.end(),greater<int>());
    sort(b.begin(), b.end(),greater<int>());

    long long h = 0, v = 0;
    long long h1 = 1, v1 = 1;
    long long ans = 0;
    // Madhur GOel

    while (h < a.size() && v < b.size()) {
        if (a[h] > b[v]) {
            h1++;
            ans = ans + ( v1 * a[h++]);
        } else {
            v1++;
            ans = ans+( h1 * b[v++] );
        }
    }
    // Madhur GOel


    while (v < b.size()) {
        ans += b[v++] * h1;
    }

    while (h < a.size()) {
        ans += a[h++] * v1;
    }


    return ans;
            // Madhur GOel


    }
};
