class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        queue = []
        for num in horizontalCut:
            queue.append([num, 0])
        for num in verticalCut:
            queue.append([num, 1])
        queue.sort(key=lambda it:-it[0])
        # print(queue)
        mul = [1, 1]
        ans = 0
        for num, dis in queue:
            ans += mul[dis] * num
            mul[dis ^ 1] += 1
        return ans
