#define PB push_back
#define pi pair<int,char>
#define ll long long
#define F first
#define all(v) v.begin(), v.end()
#define S second
class Solution {
public:
    ll minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        vector<pi> setOfCuts;
        for(auto it : hc) {
            setOfCuts.PB({it, 'h'});
        }
        for(auto it : vc) {
            setOfCuts.PB({it, 'v'});
        }
        sort(all(setOfCuts), [](const pi& a, const pi& b) {
            return a.F > b.F;
        });

        ll hC = 1, vC = 1,total = 0;
        for(auto c : setOfCuts) {
            if(c.S == 'h') {
                total += c.F * vC;
                hC++;
            } else {
                total += c.F * hC;
                vC++;
            }
        }

        return total;
    }
};
