class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& a, vector<int>& b) {
        using PII = pair<int, int>;
        vector<PII> v;
        for(auto x : a) {
            v.emplace_back(x, 0);
        }
        for(auto x : b) {
            v.emplace_back(x, 1);
        }
        sort(begin(v), end(v), [&](auto &a, auto &b) {
            return a.first > b.first; 
        });
        long long res = 0;
        int c1 = 0, c2 = 0;
        for(auto &[x, y] : v) {
            if(y == 0) {
                res += 1ll * x * (c1 + 1);
                c2 ++;
            } else {
                res += 1ll * x * (c2 + 1);
                c1 ++;
            }
        }
        return res;
    }
};
