#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    long long minimumCost(int a1, int a2, vector<int>& a3, vector<int>& a4) {
        long long a5 = 0;
        sort(a3.begin(), a3.end(), greater<int>());
        sort(a4.begin(), a4.end(), greater<int>());
        int a6 = 1, a7 = 1;
        int a8 = 0, a9 = 0;

        while (a8 < a3.size() && a9 < a4.size()) {
            if (a3[a8] > a4[a9]) {
                a5 += a3[a8] * a7;
                a6++;
                a8++;
            } else {
                a5 += a4[a9] * a6;
                a7++;
                a9++;
            }
        }

        while (a8 < a3.size()) {
            a5 += a3[a8] * a7;
            a8++;
        }

        while (a9 < a4.size()) {
            a5 += a4[a9] * a6;
            a9++;
        }

        return a5;
    }
};
