class Solution:
  def minimumCost(self, m: int, n: int, h: List[int], v: List[int]) -> int:
    h.sort()
    v.sort()
    i = j = 1
    res = 0
    while h and v:
      if h[-1] < v[-1]:
        res += j * v.pop()
        i += 1
      else:
        res += i * h.pop()
        j += 1
    while h:
      res += i * h.pop()
    while v:
      res += j * v.pop()
    return res
