class Solution {
public:
    long long minimumCost(int m, int n, std::vector<int>& a1, std::vector<int>& a2) {
    sort(a1.begin(), a1.end(), std::greater<int>());
    sort(a2.begin(), a2.end(), std::greater<int>());

        return calculateCost(a1, a2);
    }

private:
    long long calculateCost(const std::vector<int>& a1, const std::vector<int>& a2) {
        int h = 0, v = 0;
        int hh = 1, vv = 1;
        long long totalcost = 0;

        while (h < a1.size() && v < a2.size()) {
            if (a1[h] > a2[v]) {
                totalcost += static_cast<long long>(a1[h]) * vv;
                hh++;
                h++;
            } else {
                totalcost += static_cast<long long>(a2[v]) * hh;
                vv++;
                v++;
            }
        }

        while (h < a1.size()) {
            totalcost += static_cast<long long>(a1[h]) * vv;
            hh++;
            h++;
        }

        while (v < a2.size()) {
            totalcost += static_cast<long long>(a2[v]) * hh;
            vv++;
            v++;
        }

        return totalcost;
    }
};
