class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<pair<int,int>> pq;
        for(auto it:horizontalCut)
        {
            pq.push({it, 0});
        }
        for(auto it:verticalCut)
        {
            pq.push({it, 1});
        }
        long long hc=1;
        long long vc=1;
        long long ans=0;
        while(!pq.empty())
        {
            int x=pq.top().first;
            int y=pq.top().second;
            pq.pop();
            if(y==0)
            {
                ans += vc * x;
                hc++;
            }
            else
            {
                ans += hc * x;
                vc++;
            }
        }
        return ans;
    }
};
