class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<long long> hCuts(horizontalCut.begin(), horizontalCut.end());
        priority_queue<long long> vCuts(verticalCut.begin(), verticalCut.end());

        long long h = 1, v = 1;
        long long totalCost = 0;

        while (!hCuts.empty() || !vCuts.empty()) {
            if (vCuts.empty() || (!hCuts.empty() && hCuts.top() > vCuts.top())) {
                totalCost += hCuts.top() * v;
                hCuts.pop();
                h++;
            } else {
                totalCost += vCuts.top() * h;
                vCuts.pop();
                v++;
            }
        }

        return totalCost;

    }
};
