/**
 * @param {number} m
 * @param {number} n
 * @param {number[]} horizontalCut
 * @param {number[]} verticalCut
 * @return {number}
 */
var minimumCost =  function(m, n, hcut, vcut) {
    let res = 0;
    let hcount = 1;
    let vcount = 1;
    hcut.sort((a, b) => b - a)
    vcut.sort((a, b) => b - a)
    let hi = 0;
    let vi = 0;
    while(hi < hcut.length || vi < vcut.length) {
        if (hi >= hcut.length) {
            res += vcut[vi] * hcount
            vi += 1;
            vcount += 1;
            continue;
        }

        if (vi >= vcut.length) {
            res += hcut[hi] * vcount
            hi += 1;
            hcount += 1;
            continue;
        }

        if (vcut[vi] > hcut[hi]) {
            res += vcut[vi] * hcount
            vi += 1;
            vcount += 1;
        }
        else {
            res += hcut[hi] * vcount
            hi += 1;
            hcount += 1;
        }
    }
    return res
};
