class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<int, char>> cuts;
        for (int cost : h) 
            cuts.push_back({cost, 'H'});  
        for (int cost : v) 
            cuts.push_back({cost, 'V'});  
        sort(cuts.rbegin(), cuts.rend());
        long long hp = 1, vp = 1, ans = 0;
        for (auto& cut : cuts) {
            if (cut.second == 'H') {
                ans += static_cast<long long>(cut.first) * vp;
                hp++;
            } else {
                ans += static_cast<long long>(cut.first) * hp;
                vp++;
            }
        }
        return ans;
    }
};
