class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& ha, vector<int>& va) {
        int i,j,x,y;
        vector<pair<int,int> > a;
        a.clear();
        long long ans;
        long long sum[2];
        ans=0;
        sum[0]=0;sum[1]=0;
        for (i=0;i<ha.size();i++)
        {
            a.push_back(make_pair(ha[i],0));
        }
        for (i=0;i<va.size();i++)
            a.push_back(make_pair(va[i],1));
        sort(a.begin(),a.end());
        for (i=0;i<a.size();i++)
        {
            x=a[i].first;
            y=a[i].second;
            ans+=sum[1-y];
            ans+=x;
            sum[y]+=x;
        }
        return ans;
    }
};
