class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<long long, int>> cuts; // {cost, type}

        for(int i = 0; i < horizontalCut.size(); i++) {
            cuts.push_back({horizontalCut[i], 0});
        }

        for(int i = 0; i < verticalCut.size(); i++) {
            cuts.push_back({verticalCut[i], 1});
        }

        sort(cuts.begin(), cuts.end(), greater<pair<long long, int>>());

        long long totalCost = 0;
        long long hPieces = 1, vPieces = 1;

        for(auto& cut : cuts) {
            if (cut.second == 0) {
                totalCost += cut.first * vPieces;
                hPieces++;
            } else {
                totalCost += cut.first * hPieces;
                vPieces++;
            }
        }

        return totalCost;
    }
};
