class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(),h.end(),greater<>());
        sort(v.begin(),v.end(),greater<>());
        int i=0,j=0;
        long long cost = 0;
        long long hw = 1;
        long long vw = 1;
        while(i<(int)h.size() && j<(int)v.size()){
            if(h[i] >= v[j]){
                cost += h[i]*1LL*vw;
                hw++;
                i++;
            }else{
                cost += v[j]*1LL*hw;
                vw++;
                j++;
            }
        }
        long long total = 0;
        while(i<(int)h.size()){
            total += h[i++];
        }
        cost = cost + total*1LL*vw;

        total = 0;
        while(j<(int)v.size()){
            total += v[j++];
        }
        cost = cost + total*1LL*hw;
        return cost;
    }
};

