class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.begin(), horizontalCut.end());
        sort(verticalCut.begin(), verticalCut.end());

        long long i = m - 2, j = n - 2;
        long long horizontal = 1, vertical = 1, ans = 0;

        while(i >= 0 && j >= 0) {
            if(horizontalCut[i] > verticalCut[j]) {
                ans += horizontalCut[i] * horizontal;
                vertical++;
                i--;
            }
            else {
                ans += verticalCut[j] * vertical;
                horizontal++;
                j--;
            }
        }

        while(i >= 0)
            ans += horizontalCut[i--] * horizontal;

        while(j >= 0)
            ans += verticalCut[j--] * vertical;

        return ans;
    }
};
