typedef pair<int, int> Pii;
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<Pii> v;
        for(auto c:horizontalCut)
            v.emplace_back(c, 0);
        for(auto c:verticalCut)
            v.emplace_back(c, 1);
        sort(v.rbegin(), v.rend());
        int cnt[2] = {1, 1};
        long long ans = 0;
        for(auto c:v){
            ans += c.first * cnt[c.second];
            cnt[c.second ^ 1] ++;
        }
        return ans;
    }
};
