class Solution:
    def minimumCost(
        self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]
    ) -> int:
        temp = []
        for h in horizontalCut:
            temp.append((h, "h"))

        for v in verticalCut:
            temp.append((v, "v"))

        temp.sort(key=lambda x: x[0], reverse=True)

        hn, vn = 1, 1
        res = 0
        for t in temp:
            d, f = t[0], t[1]
            if f == "h":
                res += d * vn
                hn += 1

            else:
                res += d * hn
                vn += 1

        return res
