class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& h, vector<int>& v) {
        int r = 1, c = 1;
        vector<pair<int, int>> a;
        for (auto& e: h) 
            a.emplace_back(e, 1);
        for (auto& e: v)
            a.emplace_back(e, 0);
        sort(a.rbegin(), a.rend());
        int64_t ans = 0;
        for (auto& e: a) {
            int64_t val = e.first, type = e.second;
            if (type) {
                ans += c * val;
                r++;
            } else {
                ans += r * val;
                c++;
            }
        }
        return ans;
    }
};
