class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& aadu, vector<int>& ubhu) 
    {
        sort(aadu.rbegin(), aadu.rend());
        sort(ubhu.rbegin(), ubhu.rend());

        long long ans = 0;
        int cntH = 1;
        int cntV = 1;

        int i = 0, j = 0;

        while (i < aadu.size() && j < ubhu.size()) 
        {
            if (aadu[i] >= ubhu[j]) 
            {
                ans += aadu[i] * cntV*1ll;
                i++;
                cntH++;
            } 
            else 
            {
                ans += ubhu[j] * cntH*1ll;
                j++;
                cntV++;
            }
        }

        while (i < aadu.size()) {
            ans += aadu[i] * cntV*1ll;
            i++;
            cntH++;
        }

        while (j < ubhu.size()) {
            ans += ubhu[j] * cntH*1ll;
            j++;
            cntV++;
        }

        return ans;
    }
};
