class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long a=1,b=1,c=0;
        sort(h.rbegin(),h.rend());
        sort(v.rbegin(),v.rend());
        int i=0,j=0;
        while(i<h.size()&&j<v.size()){
            if(h[i]>v[j]){
                c+=1LL*h[i]*b;
                i++;
                a++;
            } else {
                c+=1LL*v[j]*a;
                j++;
                b++;
            }
        }
        while(i<h.size()){
            c+=1LL*h[i]*b;
            i++;
        }
        while(j<v.size()){
            c+=1LL*v[j]*a;
            j++;
        }
        return c;
    }
};
