class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        if m == 1 and n == 1:
            return 0
        if m == 1:
            return sum(verticalCut)
        if n == 1:
            return sum(horizontalCut)

        h_num, v_num = 1, 1
        horizontalCut.sort()
        verticalCut.sort()
        i, j = m - 2, n - 2
        ans = 0
        while i >= 0 and j >= 0:
            if horizontalCut[i] >= verticalCut[j]:
                h_num += 1
                ans += horizontalCut[i] * v_num
                i -= 1
            else:
                v_num += 1
                ans += verticalCut[j] * h_num
                j -= 1

        if i >= 0:
            return ans + sum(horizontalCut[0: i + 1]) * v_num
        else:
            return ans + sum(verticalCut[0: j + 1]) * h_num
