public class Solution {
    public long MinimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        Array.Sort(horizontalCut);
        Array.Sort(verticalCut);
        long hMult = 1;
        long vMult = 1;
        int hNext = m-2;
        int vNext = n-2;
        long result = 0;
        while (hNext >= 0 || vNext >= 0)
        {
            if (hNext < 0 || (vNext >= 0 && horizontalCut[hNext] <= verticalCut[vNext]))
            {
                result += (long)verticalCut[vNext] * vMult;
                vNext--;
                hMult++;
                continue;
            }
            if (vNext < 0 || verticalCut[vNext] <= horizontalCut[hNext])
            {
                result += (long)horizontalCut[hNext] * hMult;
                hNext--;
                vMult++;
                continue;
            }
        }
        return result;
    }
}
