class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& v, vector<int>& vv) {
        sort(v.begin(), v.end());
        sort(vv.begin(), vv.end());
        reverse(v.begin(), v.end());
        reverse(vv.begin(), vv.end());
        long long ans1 = 0, ans2 = 0;
        long long cnt1 = 1, cnt2 = 1; 
        long long sum = 0;
        while (ans1 < v.size() && ans2 < vv.size()) {
            if (v[ans1] > vv[ans2]) {
                sum += v[ans1] * cnt2;
                ans1++;
                cnt1++;
            } else {
                sum += vv[ans2] * cnt1;
                ans2++;
                cnt2++;
            }
        }
        while (ans1 < v.size()) {
            sum += v[ans1] * cnt2;
            ans1++;
            cnt1++;
        }
        while (ans2 < vv.size()) {
            sum += vv[ans2] * cnt1;
            ans2++;
            cnt2++;
        }

        return sum;
    }
};
