class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {




         int hcount=1;
        int vcount=1;

        vector<pair<int,int>>sm;

        for(auto it:h){
sm.push_back({it,1});

        }
               for(auto it:v){
sm.push_back({it,2});

        }

        sort(sm.rbegin(),sm.rend());

        long long ans=0;

        for(auto it:sm){

            if(it.second==1){

            ans+=vcount*it.first;
                hcount++;
            }
            else{
 ans+=hcount*it.first;
                vcount++;
            }

        }

        return ans;

    }
};
