class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        h_pieces = 1
        v_pieces = 1
        total_cost = 0

        max_heap = []
        for cost in horizontalCut:
            heapq.heappush(max_heap, (-cost, 'h'))
        for cost in verticalCut:
            heapq.heappush(max_heap, (-cost, 'v'))

        while max_heap:
            cost, cut_type = heapq.heappop(max_heap)
            if cut_type == 'h':
                total_cost += -cost * v_pieces
                h_pieces += 1
            else:
                total_cost += -cost * h_pieces
                v_pieces += 1

        return total_cost
