class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<vector<long long>> v;
        long long cx=0,cy=0,res=0;
        for(auto& h:horizontalCut){
            v.push_back({h,1});
        }
        for(auto& h:verticalCut){
            v.push_back({h,2});
        }
        sort(v.begin(),v.end());
        long long k=v.size();
        for(long long i=k-1;i>=0;--i){
            if(v[i][1]==1){
                res+=v[i][0]*(cx+1);
                cy++;
            }
            else{
                res+=v[i][0]*(cy+1);
                cx++;
            }
        }
        return res;
    }
};
