class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,char>> mp;
        for(auto &cost: horizontalCut){
            mp.push_back({cost,'h'});
        }
        for(auto &cost: verticalCut){
            mp.push_back({cost,'v'});
        }
        sort(mp.begin(),mp.end());
        int horipi=1,vertipi=1;
        long long mincost=0LL;
        for(int i=n+m-3;i>=0;i--){
            if(mp[i].second=='h'){
                mincost= mincost + (mp[i].first*vertipi);
                horipi++;
            }
            else{
                mincost= mincost + (mp[i].first*horipi);
                vertipi++;
            }
        }
        return mincost;
    }
};
