typedef long long ll;

class Solution {
public:
    ll ans;
    long long minimumCost(int m, int n, vector<int>& r, vector<int>& c) {
        int rc = 1, lc = 1;
        r.push_back(-1); c.push_back(-1);
        sort(r.begin(), r.end());
        sort(c.begin(), c.end());
        for (int k = 0; k < (n + m) - 2; k++) {
            int rm = r[r.size()-1], cm = c[c.size()-1];
            if (cm >= rm) {
                ans = ans + 1ll * cm * (lc);
                c.pop_back();
                rc++;
            } else {
                ans = ans + 1ll * rm * rc;
                r.pop_back();
                lc++;
            }
            // cout << ans << "\n";
        }
        return ans;
    }
};
