class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        long horiMult = 1;
        long vertMult = 1;

        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);

        reverse(horizontalCut);
        reverse(verticalCut);

        int i = 0;
        int j = 0;
        long cost = 0;

        while(i < m - 1 && j < n - 1) {
            if(horizontalCut[i] >= verticalCut[j]) {
                cost += horizontalCut[i] * horiMult;
                vertMult++;
                i++;
            } else {
                cost += verticalCut[j] * vertMult;
                horiMult++;
                j++;
            }
        }

        while(i < m - 1) {
            cost += horizontalCut[i] * horiMult;
            i++;
        }

        while(j < n - 1) {
            cost += verticalCut[j] * vertMult;
            j++;
        }

        return cost;
    }

    void reverse(int[] arr) {
        int i = 0;
        int j = arr.length - 1;

        while(i < j) {
            int temp = arr[i];
            arr[i] = arr[j];
            arr[j] = temp;
            i++;
            j--;
        }
    }
}
