class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<int, char>> cuts;

        for (int i = 0; i < h.size(); i++) {
            cuts.push_back({h[i], 'H'});
        }

        for (int j = 0; j < v.size(); j++) {
            cuts.push_back({v[j], 'V'});
        }

        sort(cuts.rbegin(), cuts.rend());

        long long hp = 1, vp = 1;
        long long ret = 0;

        for (auto& cut : cuts) {
            if (cut.second == 'H') {
                ret += cut.first * vp;
                hp++;
            } else {
                ret += cut.first * hp;
                vp++;
            }
        }

        return ret;
    }
};
