typedef long long ll;
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hcs, vector<int>& vcs) {

        sort(hcs.begin(), hcs.end());
        sort(vcs.begin(), vcs.end());

        ll answer = 0;
        ll hpieces = 1, vpieces = 1;
        while (!hcs.empty() && !vcs.empty()) {
            if (hcs.back() > vcs.back()) {
                answer += ll(hcs.back()) * vpieces;

                ++hpieces;
                hcs.pop_back();
            } else {
                answer += ll(vcs.back()) * hpieces;

                ++vpieces;
                vcs.pop_back();
            }
        }

        ll leftovers = 0;
        while (!hcs.empty()) {
            leftovers += hcs.back();
            hcs.pop_back();
        }
        answer += leftovers * vpieces;

        leftovers = 0;
        while (!vcs.empty()) {
            leftovers += vcs.back();
            vcs.pop_back();
        }
        answer += leftovers * hpieces;

        return answer;
    }
};
