class Solution {
public:
    #define ll long long int
    long long minimumCost(int m, int n, vector<int>& hv, vector<int>& vv) {
        ll h=1;
        ll v=1;
        priority_queue<ll>hpq;
        priority_queue<ll>vpq;
        for(auto x:hv){
            hpq.push(x);
        }
        for(auto y:vv){
            vpq.push(y);
        }
        ll ans=0;
        while(!hpq.empty() and !vpq.empty()){
            if(hpq.top() > vpq.top()){
                ll x= hpq.top();
                hpq.pop();
                ans+=h*x;
                v+=1;
            }
            else{
                ll x=vpq.top();
                vpq.pop();
                ans+=v*x;
                h+=1;
            }
        }

        while(!hpq.empty()){
            ll x= hpq.top();
            hpq.pop();
            ans+=h*x;
        }

        while(!vpq.empty()){
            ll x= vpq.top();
            vpq.pop();
            ans+=v*x;
        }
        return ans;
    }
};
