class Solution {
public:
    long long minimumCost(int m, int num, vector<int>& hC, vector<int>& vC) {
        sort(hC.begin(), hC.end(), greater<int>());
        sort(vC.begin(), vC.end(), greater<int>());

        int hI = 0, vI = 0;
        long long tC = 0;
        int hP = 1, vP = 1;

        while (hI < hC.size() && vI < vC.size()) {
            if (hC[hI] >= vC[vI]) {
                tC += hC[hI] * vP;
                hI++;
                hP++;
            } else {
                tC += vC[vI] * hP;
                vI++;
                vP++;
            }
        }

        while (hI < hC.size()) {
            tC += hC[hI] * vP;
            hI++;
        }

        while (vI < vC.size()) {
            tC += vC[vI] * hP;
            vI++;
        }

        return tC;
    }
};
