class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int, bool>> vp;
        for (auto &v: horizontalCut)
            vp.emplace_back(v, 0);
        for (auto &v: verticalCut)
            vp.emplace_back(v, 1);
        sort(vp.begin(), vp.end());
        reverse(vp.begin(), vp.end());
        long long wh = 1, wv = 1, res = 0;
        for (auto &[val, dir]: vp) {
            if (dir == 0) { // h
                ++wv;
                res += wh * val;
            } else { // v
                ++wh;
                res += wv * val;
            }
        }
        return res;
    }
};
