#pragma GCC optimize(Ofast)
#include <bits/stdc++.h>
using namespace std;
#define ff first
#define ss second
#define mp make_pair
#define ll long long
#define ld long double
#define rep(i, n) for (ll i = 0; i < n; i++)
#define rev(i, n) for (ll i = n; i >= 0; i--)
#define rep_a(i, a, n) for (ll i = a; i < n; i++)
#define pb push_back
#define all(_obj) _obj.begin(), _obj.end()
#define pll pair<ll, ll>
#define vll vector<ll>
#define vvll vector<vll>
#define vpll vector<pll>
#define pc(n) __builtin_popcountll(n)
#define endl "\n"
const ll N = 1e5 + 11, mod = 1e9 + 7, mod2 = 3037000493ll;

class Solution
{
public:
    long long minimumCost(int m, int n, vector<int> &horizontalCut, vector<int> &verticalCut)
    {
        vpll cuts;
        for (auto &i : horizontalCut)
        {
            cuts.push_back({i, 0});
        }
        for (auto &i : verticalCut)
        {
            cuts.push_back({i, 1});
        }

        sort(cuts.rbegin(), cuts.rend());

        ll hp = 1, vp = 1;

        long long ans = 0;
        for (auto &cut : cuts)
        {
            ll cost = cut.ff;
            ll hor = cut.ss;

            if (!hor)
            {
                ans += (cost)*vp;
                hp++;
            }
            else
            {
                ans += (cost)*hp;
                vp++;
            }
        }

        return (ans);
    }
};
