class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        priority_queue<pair<long long,int>> q;
        for(auto c:h) q.push({c,0});
        for(auto c:v) q.push({c,1});
        int r=1,c=1;
        long long ans=0;
        while(!q.empty()){
            auto [x,i]=q.top();
            q.pop();
            if(i==0){
                ans+=x*c;
                r++;
            }else{
                ans+=x*r;
                c++;
            }
        }
        return ans;
    }
};
