class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end());
        sort(v.begin(), v.end());
        reverse(h.begin(), h.end());
        reverse(v.begin(), v.end());
        int h_idx = 0, v_idx = 0;
        long long sum = 0, h_pic = 1, v_pic = 1;
        while(h_idx < m - 1 && v_idx < n-1) {
            if(h[h_idx] > v[v_idx]) {
                sum += h[h_idx++] * v_pic;
                h_pic++;
            }
            else {
                sum += v[v_idx++] * h_pic;
                v_pic++;
            }
        }
        while(h_idx < m - 1) {
            sum += h[h_idx++] * v_pic;
        }
        while(v_idx < n - 1) {
            sum += v[v_idx++] * h_pic;
        }
        return sum;
    }
};
