/**
    Time complexity: O(m x log m + n log n)
    Space complexity: O(1)
*/
function minimumCost(m: number, n: number, horizontalCut: number[], verticalCut: number[]): number {
    horizontalCut.sort((x, y) => y - x)
    verticalCut.sort((x, y) => y - x)
    let idxI = 0
    let idxJ = 0
    let minimum = 0
    let vertical = 1
    let horizontal = 1
    while(idxI < m -1 && idxJ < n -1) {
        if(horizontalCut[idxI] > verticalCut[idxJ]) {
            minimum += horizontalCut[idxI] * vertical
            idxI++
            horizontal++
        } else {
            minimum += verticalCut[idxJ] * horizontal
            idxJ++
            vertical++
        }
    }  
    while(idxI < m -1) {
        minimum += horizontalCut[idxI] * vertical
        idxI++
        horizontal++
    }
    while(idxJ < n -1) {
        minimum += verticalCut[idxJ] * horizontal
        idxJ++
        vertical++
    }
    return minimum
};
