typedef long long ll;
typedef long double ld;
const ll MOD = 1e9 + 7;

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {

        ll res = 0;
        int h = 0, v = 0;
        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());
        while(v + h != m + n - 2) {
            if(v == n - 1|| h != m - 1 && horizontalCut[h] > verticalCut[v]) {
                res += 1LL * horizontalCut[h] * (v + 1);
                // cout << "h : " << horizontalCut[h] << '\n';
                h++;
            } else {
                res += 1LL * verticalCut[v] * (h + 1);
                // cout << "v : " << verticalCut[v] << '\n';
                v++;
            }


        }
        return res;

    }
};
