class Solution {
    // long
    public long minimumCost(int m, int n, int[] h, int[] v) {
        Arrays.sort(h);
        Arrays.sort(v);
        int i = m - 2, j = n - 2;
        long hc = 1, vc = 1, ans = 0;

        while (i >= 0 && j >= 0) {
            // System.out.println(i + " " + j);
            if (h[i] > v[j]) {
                ans += h[i--] * vc;
                hc++;
            } else {
                ans += v[j--] * hc;
                vc++;
            }
        }

        while (i >= 0) {
            ans += h[i--] * vc;
        }
        while (j >= 0) {
            ans += v[j--] * hc;
        }

        return ans;         
    }
}
