class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        heapify(horizontalCut)
        heapify(verticalCut)

        cost = 0
        while horizontalCut and verticalCut:
            if horizontalCut[0] < verticalCut[0]:
                cost += heappop(horizontalCut) * (1 + len(verticalCut))
            else:
                cost += heappop(verticalCut) * (1 + len(horizontalCut))
        cost += sum(horizontalCut) + sum(verticalCut)
        return cost
